"use strict";

let modImg  =   [];
let wiresImg    =   [];

let disTIM, disBTN, disWIR, disWAG;
let disBlaWire, disCyaWire, disMagWire, disYelWire, disRedWire;
let disBlaWireH, disCyaWireH, disMagWireH, disYelWireH, disRedWireH;


let positions    =   [];

let module  =   {
    x:  undefined,
    y:  undefined,
    size:   128,
}

let timerMod;
let timerMaxMin;
let timerMaxSec;

let buttonMod;
let wireMod;
let wagMod;


function preload()  {
    for (let i = 0; i < 4; i++) {
        modImg[i]   =   loadImage(`assets/images/Module_${i}.png`);
    }

    for (let i = 0; i < 10; i++) {
        wiresImg[i]   =   loadImage(`assets/images/Wires_${i}.png`);
    }
}

function setup()    {
    createCanvas(windowHeight, windowHeight);

    imageSetup();


    timerMaxMin =   floor(random(3, 8));
    timerMaxSec =   60*timerMaxMin;

    for (let i = 0; i < 4; i++) {
        positions[i]    =   position(width*(i + 1)/5, height/2);
        //console.log(positions[i]);
    }


    timerMod    =   new Timer(positions[0].x, positions[0].y, positions[0].size, timerMaxMin, timerMaxSec);
    buttonMod   =   new Button(positions[1].x, positions[1].y, positions[1].size);
    wireMod     =   new Wire(positions[2].x, positions[2].y, positions[2].size);
    wagMod      =   new WordAssociationGame(positions[3].x, positions[3].y, positions[3].size);

    buttonMod.indicatorColor();
    wagMod.wordGenerator();

    wireMod.cableSetup();
}

function position(x, y)   {
    let module  =   {
        x:  x,
        y:  y,
        size:   128,
    };

    return module;
}

function imageSetup()   {
    //modules:
    disTIM  =   modImg[0];
    disBTN  =   modImg[1];
    disWIR  =   modImg[2];
    disWAG  =   modImg[3];

    //Wires:
    disBlaWire  =   wiresImg[0];
    disBlaWireH =   wiresImg[1];
    disCyaWire  =   wiresImg[2];
    disCyaWireH =   wiresImg[3];
    disMagWire  =   wiresImg[4];
    disMagWireH =   wiresImg[5];
    disYelWire  =   wiresImg[6];
    disYelWireH =   wiresImg[7];
    disRedWire  =   wiresImg[8];
    disRedWireH =   wiresImg[9];
}

function draw() {
    background(100);

    timerMod.countdown();


    timerMod.display();
    buttonMod.displayMod();
    wireMod.displayMod();
    wagMod.displayMod();
}

function beep() {
    let beepSFX = new Audio("data:audio/wav;base64,");
    beepSFX.play();
}